import React from "react";
import "./AddEditProductForm.css";
import categories from "../categories.json";

function AddEditProductForm({ handleCloseModal }) {
  const [name, setName] = React.useState(``);
  const [price, setPrice] = React.useState(0);
  const [category, setCategory] = React.useState("");
  const [errors, setErrors] = React.useState({
    name: null,
    price: null,
    category: null,
  });

  function handleSubmit(event) {
    event.preventDefault();

    const errors = {
      name: null,
      price: null,
      category: null,
    };

    if (name.length === 0) {
      errors.name = "Product Name Cannon be Empty";
    }
    if (price <= 0) {
      errors.price = "Product Price Must Be Greater than 0";
    }
    if (!category || category === "all") {
      errors.category = "Product Category Must not be Empty or All";
    }
    if (errors.name || errors.price || errors.category) {
      setErrors(errors);
      return;
    }
  }
  return (
    <div className="add-edit-product-form-container">
      <h1>Add Product</h1>
      <form onSubmit={handleSubmit} className="product-form">
        <label>
          Name<span className="required">*</span>:
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className={errors.name ? "invalid" : ""}
          />
          {errors.name ? <span className="required">{errors.name}</span> : null}
        </label>
        <label>
          Price<span className="required">*</span>:
          <input
            type="text"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            className={errors}
          />
        </label>
        <label>
          Category<span className="required">*</span>:
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value=""></option>
            {categories.map((category) => {
              return (
                <option value={category.value} key={category.value}>
                  {category.label}
                </option>
              );
            })}
          </select>
        </label>
        <button>SAVE & CLOSE</button>
      </form>
      <button onClick={handleCloseModal}>CLOSE</button>
    </div>
  );
}
export default AddEditProductForm;
